import React from "react";
import { useState, useEffect } from "react";
import { Button, Form } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useLocation } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import Swal from "sweetalert2";

function FormPaket() {
  const [dtjnsform, setJnsform] = useState("tambah");
  const [dtsession, setDtsession] = useState(null);
  const [dtiduser, setIdUser] = useState("");
  const [dtnama, setNama] = useState("");
  const [dtrole, setRole] = useState("");
  const history = useNavigate();
  const location = useLocation();

  const [dtgambar, setGambar] = useState(null);

  const [inputs, setInputs] = useState({
    id_user: 0,
    nama_paket: "",
    kecepatan: 0,
    harga: 0,
    disc: 0,
    status: 0,
  });

  // On file select (from the pop up)
  onFileChange = (event) => {
    // Update the state
    this.setState({ selectedFile: event.target.files[0] });
  };

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs((values) => ({ ...values, [name]: value }));
  };

  const SaveForm = async (e) => {
    e.preventDefault();

    console.log(
      "------------------------------test wini----------------------------------"
    );
    console.log(inputs.nama_paket);

    let token = sessionStorage.getItem("access_token");
    let result = await fetch("http://127.0.0.1:8000/api/create_paket", {
      method: "POST",
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        id_user: inputs.id_user,
        nama_paket: inputs.nama_paket,
        kecepatan: inputs.kecepatan,
        harga: inputs.harga,
        disc: inputs.disc,
        status: inputs.status,
      }),
    })
      .then((res) => res.json())
      .then((json) => {
        console.log("ini respon");
        console.log(json);

        if (json.hasOwnProperty("error")) {
          if (!json.error) {
            Swal.fire({
              position: "top-center",
              icon: "success",
              title: "Success",
              html: json.msg,
              showConfirmButton: false,
              timer: 1500,
            });
            history("/dashboard");
          } else {
            Swal.fire({
              position: "top-center",
              icon: "error",
              title: "Failed",
              showConfirmButton: false,
              html: json.msg,
              timer: 1500,
            });
          }
        } else {
          Swal.fire({
            position: "top-center",
            icon: "error",
            title: "Login Failed",
            showConfirmButton: false,
            html: json.message,
            timer: 1500,
          });
        }
      });
  };

  useEffect(() => {
    if (
      sessionStorage.getItem("access_token") == null ||
      sessionStorage.getItem("access_token") == "undefined"
    ) {
      Swal.fire({
        position: "top-center",
        icon: "info",
        title: "Just Info",
        html: "Anda belum login",
        showConfirmButton: false,
        timer: 2000,
      });
      history("/");
    } else {
      let get_dtsession = JSON.parse(sessionStorage.getItem("data"));
      setDtsession(get_dtsession);
      setIdUser(get_dtsession.data.id);
      setNama(get_dtsession.data.name);
      setRole(get_dtsession.data.role);

      if (get_dtsession.data.role != "admin") {
        Swal.fire({
          position: "top-center",
          icon: "info",
          title: "Just Info",
          html: "Anda tidak bisa mengubah CRUD Paket, Anda bukan admin",
          showConfirmButton: false,
          timer: 2000,
        });
        history("/");
      }

      setJnsform(location.state.jnsForm);
      setInputs((prevState) => ({
        ...prevState,
        id_user: get_dtsession.data.id,
      }));
    }
  }, []);

  return (
    <div className="container mt-4 shadow-sm p-3 mb-5 bg-white rounded">
      <h3>Form {dtjnsform} produk paket</h3>
      <br />
      <Form onSubmit={SaveForm}>
        <Form.Group className="mb-3" controlId="formBasicDisc">
          <Form.Label>Gambar Produk Paket</Form.Label>
          <Form.Control
            type="file"
            name="disc"
            placeholder="Apakah ada diskon?"
            // value={email}
            onChange={handleChange}
          />
          <Form.Text className="text-muted"></Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicNamaPaket">
          <Form.Label>Nama Paket</Form.Label>
          <Form.Control
            required
            type="text"
            name="nama_paket"
            placeholder="Tuliskan nama paket"
            // value={email}
            onChange={handleChange}
          />
          <Form.Text className="text-muted"></Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicKecepatan">
          <Form.Label>Kecepatan Internet (dalam Mbps)</Form.Label>
          <Form.Control
            required
            type="number"
            name="kecepatan"
            placeholder="Kecepatan internet"
            // value={email}
            onChange={handleChange}
          />
          <Form.Text className="text-muted"></Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicHarga">
          <Form.Label>Harga Paket</Form.Label>
          <Form.Control
            required
            type="number"
            name="harga"
            placeholder="Harga Paket"
            // value={email}
            onChange={handleChange}
          />
          <Form.Text className="text-muted"></Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicDisc">
          <Form.Label>Diskon</Form.Label>
          <Form.Control
            type="number"
            name="disc"
            placeholder="Apakah ada diskon?"
            // value={email}
            onChange={handleChange}
          />
          <Form.Text className="text-muted"></Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicStatus">
          <Form.Label>Status Paket</Form.Label>
          <Form.Select
            aria-label="Default select example"
            required
            type="number"
            name="status"
            // value={email}
            onChange={handleChange}
          >
            <option value="">-- Pilih Status --</option>
            <option value="1">Publish</option>
            <option value="2">Draft</option>
          </Form.Select>
        </Form.Group>

        <Button variant="primary" type="submit">
          Simpan
        </Button>
      </Form>
    </div>
  );
}

export default FormPaket;
