import React from "react";
import { useState, useEffect } from "react";
import { Button, Form } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import Swal from "sweetalert2";

export default function FormPaket() {
  const [dtjnsform, setJnsform] = useState("tambah");
  const [dtsession, setDtsession] = useState(null);
  const [dtnama, setNama] = useState("");
  const [dtrole, setRole] = useState("");
  const history = useNavigate();

  useEffect(() => {
    if (
      sessionStorage.getItem("access_token") == null ||
      sessionStorage.getItem("access_token") == "undefined"
    ) {
      Swal.fire({
        position: "top-center",
        icon: "info",
        title: "Just Info",
        html: "Anda belum login",
        showConfirmButton: false,
        timer: 2000,
      });
      history("/");
    } else {
      let get_dtsession = JSON.parse(sessionStorage.getItem("data"));
      setDtsession(get_dtsession);
      setNama(get_dtsession.data.name);
      setRole(get_dtsession.data.role);

      if (get_dtsession.data.role != "admin") {
        Swal.fire({
          position: "top-center",
          icon: "info",
          title: "Just Info",
          html: "Anda tidak bisa mengubah CRUD Paket, Anda bukan admin",
          showConfirmButton: false,
          timer: 2000,
        });
        history("/");
      }
    }
  }, []);

  const Form = async (e) => {
    e.preventDefault();
    let result = await fetch("http://127.0.0.1:8000/api/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        email: email,
        password: password,
      }),
    })
      .then((res) => res.json())
      .then((json) => {
        // console.log("ini respon");
        // console.log(email);
        // console.log(password);

        //set Session Storage
        sessionStorage.setItem("access_token", json.access_token);
        sessionStorage.setItem("data", JSON.stringify(json));

        if (json.hasOwnProperty("error")) {
          if (!json.error) {
            Swal.fire({
              position: "top-center",
              icon: "success",
              title: "Login Success",
              showConfirmButton: false,
              timer: 1500,
            });
            history("/dashboard");
          } else {
            Swal.fire({
              position: "top-center",
              icon: "error",
              title: "Login Failed",
              showConfirmButton: false,
              html: json.message,
              timer: 1500,
            });
          }
        } else {
          Swal.fire({
            position: "top-center",
            icon: "error",
            title: "Login Failed",
            showConfirmButton: false,
            html: json.message,
            timer: 1500,
          });
        }
      });
  };

  return (
    <div className="container mt-4 shadow-sm p-3 mb-5 bg-white rounded">
      <h3>Form Tambah Paket</h3>
      <br />
      <Form onSubmit={Auth}>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control
            type="email"
            placeholder="Enter email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <Form.Text className="text-muted"></Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicCheckbox">
          <Form.Check type="checkbox" label="Check me out" />
        </Form.Group>
        <Button variant="primary" type="submit">
          Submit
        </Button>
        <br />
        <br />
        <p>
          Belum punya akun?{" "}
          <Link to={"/register"}>
            <b style={{ color: "darkblue" }}>Register disini</b>
          </Link>
        </p>
      </Form>
    </div>
  );
}
