        if (json.hasOwnProperty("error")) {
          if (!json.error) {
            Swal.fire({
              position: "top-center",
              icon: "success",
              title: "Success",
              html: json.msg,
              showConfirmButton: false,
              timer: 1500,
            });
            history("/dashboard");
          } else {
            Swal.fire({
              position: "top-center",
              icon: "error",
              title: "Failed",
              showConfirmButton: false,
              html: json.msg,
              timer: 1500,
            });
          }
        } else {
          Swal.fire({
            position: "top-center",
            icon: "error",
            title: "Login Failed",
            showConfirmButton: false,
            html: json.message,
            timer: 1500,
          });
        }
      })
      .catch((error) => alert("error", error));

    // let result = await fetch("http://127.0.0.1:8000/api/create_paket", {
    //   method: "POST",
    //   headers: {
    //     Authorization: `Bearer ${token}`,
    //     "Content-Type": "application/json",
    //     Accept: "application/json",

    //     // "Content-Type": "multipart/form-data",
    //     // Accept: "application/json",
    //     // "X-Requested-With": "XMLHttpRequest",
    //   },
    //   body: JSON.stringify({
    //     id_user: inputs.id_user,
    //     nama_paket: inputs.nama_paket,
    //     kecepatan: inputs.kecepatan,
    //     harga: inputs.harga,
    //     disc: inputs.disc,
    //     status: inputs.status,
    //     gambar_produk: dtgambar,
    //   }),
    // })
    //   .then((res) => res.json())
    //   .then((json) => {
    //     console.log("ini respon");
    //     console.log(json);

    //     if (json.hasOwnProperty("error")) {
    //       if (!json.error) {
    //         Swal.fire({
    //           position: "top-center",
    //           icon: "success",
    //           title: "Success",
    //           html: json.msg,
    //           showConfirmButton: false,
    //           timer: 1500,
    //         });
    //         history("/dashboard");
    //       } else {
    //         Swal.fire({
    //           position: "top-center",
    //           icon: "error",
    //           title: "Failed",
    //           showConfirmButton: false,
    //           html: json.msg,
    //           timer: 1500,
    //         });
    //       }
    //     } else {
    //       Swal.fire({
    //         position: "top-center",
    //         icon: "error",
    //         title: "Login Failed",
    //         showConfirmButton: false,
    //         html: json.message,
    //         timer: 1500,
    //       });
    //     }
    //   });
  };

  useEffect(() => {
    if (
      sessionStorage.getItem("access_token") == null ||
      sessionStorage.getItem("access_token") == "undefined"
    ) {
      Swal.fire({
        position: "top-center",
        icon: "info",
        title: "Just Info",
        html: "Anda belum login",
        showConfirmButton: false,
        timer: 2000,
      });
      history("/");
    } else {
      let get_dtsession = JSON.parse(sessionStorage.getItem("data"));
      setDtsession(get_dtsession);
      setIdUser(get_dtsession.data.id);
      setNama(get_dtsession.data.name);
      setRole(get_dtsession.data.role);

      if (get_dtsession.data.role != "admin") {
        Swal.fire({
          position: "top-center",
          icon: "info",
          title: "Just Info",
          html: "Anda tidak bisa mengubah CRUD Paket, Anda bukan admin",
          showConfirmButton: false,
          timer: 2000,
        });
        history("/");
      }

      setJnsform(location.state.jnsForm);
      setInputs((prevState) => ({
        ...prevState,
        id_user: get_dtsession.data.id,
      }));
    }
  }, []);

  return (
    <div className="container mt-4 shadow-sm p-3 mb-5 bg-white rounded">
      <h3>Form {dtjnsform} produk paket</h3>
      <br />
      <Form onSubmit={SaveForm}>
